import { useState, useEffect, useRef } from "react";

import "./Swipper.css";

const data = [
    {tittle: "Tittle 1", color: "red"},
    {tittle: "Tittle 2", color: "blue"},
    {tittle: "Tittle 3", color: "yellow"},
    {tittle: "Tittle 4", color: "green"},
    {tittle: "Tittle 5", color: "purple"},
];

export function Swipper() {
    const [currentSlide, setCurrentSlide] = useState(0);
    const [scrollDirection, setScrollDirection] = useState('');

    const handleWheel = (event) => {
        const direction = event.deltaY < 0 ? 'up' : 'down';
        setScrollDirection(direction);
    }

    useEffect(() => {
        const container = document.getElementById('scrollable-container');
        container.addEventListener('wheel', handleWheel);

        return () => {
        container.removeEventListener('wheel', handleWheel);
        };
    }, []);

    // useEffect(() => {}, []);

    return (
        // <main ref={scrollableContainer} id="swipper-content">
        <main id="swipper-content scrollable-container">
            {data.map((item, index) => (
                <section className="swipper-section"
                style={{ "background-color": `${item.color}`, transform: `translateY(${-currentSlide * 100}%)` }}
                key={index}>
                    { item.tittle }
                    <p>El scroll est√° yendo hacia {scrollDirection}</p>
                </section>
            ))}
        </main>
    );
}
