import { useState, useEffect, useRef } from "react";

import "./Swipper.css";

const data = [
    {tittle: "Tittle 1", color: "red"},
    {tittle: "Tittle 2", color: "blue"},
    {tittle: "Tittle 3", color: "yellow"},
    {tittle: "Tittle 4", color: "green"},
    {tittle: "Tittle 5", color: "purple"},
];

export function Swipper() {
    const [currentSlide, setCurrentSlide] = useState(0);

    const handleWheel = (event) => {
        const direction = event.deltaY < 0 ? 'up' : 'down';

        // Se usa una funcion flecha por que se requiere actualizar el estado anterior
        setCurrentSlide(prevState => (direction === 'up')?(prevState - 1):(prevState + 1));
    }

    useEffect(() => {
        const container = document.getElementById('scrollable-container');
        container.addEventListener('wheel', handleWheel);

        return () => {
            container.removeEventListener('wheel', handleWheel);
        };
    }, []);

    useEffect(() => {
        console.log(currentSlide, {currentSlide});
    }, [currentSlide]);

    return (
        <main id="scrollable-container" className="swipper-content">
            <div 
                className="swipper-inner"
                style={{ transform: `translateY(${-currentSlide * 20}%)`}}
            >
                {data.map((item, index) => (
                    <section className="swipper-section"
                    style={{
                        backgroundColor: `${item.color}`,
                    }}
                    key={index}>
                        { item.tittle }
                    </section>
                ))}
            </div>
        </main>
    );
}
