import { useState, useEffect, useRef } from "react";

import "./Swipper.css";

const data = [
    {tittle: "Tittle 1", color: "red"},
    {tittle: "Tittle 2", color: "blue"},
    {tittle: "Tittle 3", color: "yellow"},
    {tittle: "Tittle 4", color: "green"},
    {tittle: "Tittle 5", color: "purple"},
];

export function Swipper() {
    const [currentSlide, setCurrentSlide] = useState(0);
    const [prevScrollPos, setPrevScrollPos] = useState(0);
    const [scrollDirection, setScrollDirection] = useState('');
    const scrollableContainer = useRef(null);

    useEffect(() => {
        function handleScroll() {
            const currentScrollPos = scrollableContainer.current.scrollTop;
            const direction = prevScrollPos > currentScrollPos ? 'up' : 'down';

            setPrevScrollPos(currentScrollPos);
            setScrollDirection(direction);
        }

        scrollableContainer.current.addEventListener('scroll', handleScroll);

        return () => {
        scrollableContainer.current.removeEventListener('scroll', handleScroll);
        };
    }, [prevScrollPos]);

    // useEffect(() => {}, []);

    return (
        <main ref={scrollableContainer} id="swipper-content">
            {data.map((item, index) => (
                <section className="swipper-section"
                style={{ "background-color": `${item.color}`, transform: `translateY(${-currentSlide * 100}%)` }}
                key={index}>
                    { item.tittle }
                    <p>El scroll est√° yendo hacia {scrollDirection}</p>
                </section>
            ))}
        </main>
    );
}
