import React, { Fragment } from "react";
import {
  motion,
  useScroll,
  useSpring,
  useTransform,
  MotionValue
} from "framer-motion"

/* Styles */
import './home.scss'
import { doc } from "prettier";

function useParallax(value, distance) {
  return useTransform(value, [0, 1], [-distance, distance]);
}

function sections({ id }) {
  const ref = useRef(null);
  const { scrollYProgress } = useScroll({ target: ref });
  const y = useParallax(scrollYProgress, 300);

  return (
    <motion.section style={{ y }}>{document.getElementsByClassName(`component${id}`)}</motion.section>
  );
}

export function Home() {
  const { scrollYProgress } = useScroll();
  const scaleX = useSpring(scrollYProgress, {
    stiffness: 100,
    damping: 30,
    restDelta: 0.001
  });

  return (
    <Fragment>
      <motion.div 
          className="progress-bar"
          style={{ scaleX: scrollYProgress }}
        />
      <div className="index">
        <div className="header-sep"></div>
        <section className="component1">
          <motion.div
            whileHover={{ scale: 1.2 }}
            whileTap={{ scale: 0.5 }}
          />
        </section>
        <section className="component2">asd</section>
        <section className="component3">asd</section>
        <section className="component4">asd</section>
      </div>
    </Fragment>
  );
}
