import { useState, useEffect } from 'react'

/* Styles */
import './opinionPagination.scss';

export function OpinionPagination({ page, setPage, max }) {
    const [input, setInput] = useState(1);

    function chgPage(pos) {
        const n = 0;
        if (pos === "next") n = 1;
        if (pos === "prev") n = -1;

        return setPage(page + n);
    }

    function next() {
        setInput(input + 1);
        if (page > max) return;
        return setPage(page + 1);
    }

    function prev() {
        setInput(input - 1);
        if (page === 1) return;
        return setPage(page - 1);
    }

    return (
        <div className="pagination">
            <button onClick={() => chgPage("prev")}>&#60;</button>
            <input type="text" value={page} />
            <button>{max}</button>
            <button onClick={next}>&#62;</button>
        </div>
    );
}
