import React, { Fragment, useState } from "react";

/* Styles */
import './slider.scss'

export function Slider() {
    const [sliderPos, setSliderPos] = useState(0);

    function goLeft() {
        if (sliderPos == 1)
        {
            setSliderPos(5);
            return;
        }
        setSliderPos(sliderPos - 1);
    }
    function goRight() {
        if (sliderPos == 5)
        {
            setSliderPos(1);
            return;
        }
        setSliderPos(sliderPos + 1);
    }

    return (
        <Fragment>
            <div className="carruselContent">
                <div className="carrusel">
                    <div>
                        <div className="imgCarrusel">Tu estas en la posicion N° {sliderPos}</div>
                        <div className="imgCarrusel">Tu estas en la posicion N° {sliderPos}</div>
                        <div className="imgCarrusel">Tu estas en la posicion N° {sliderPos}</div>
                        <div className="imgCarrusel">Tu estas en la posicion N° {sliderPos}</div>
                        <div className="imgCarrusel">Tu estas en la posicion N° {sliderPos}</div>
                    </div>
                </div>
                <div className="goLeft" onClick={() => setCount(sliderPos + 1)}></div>
                <div className="goRight"></div>
                
                <div className="carruselPagination">
                    <button className="cpDot" value="1"></button>
                    <button className="cpDot" value="2"></button>
                    <button className="cpDot" value="3"></button>
                    <button className="cpDot" value="4"></button>
                    <button className="cpDot" value="5"></button>
                </div>
            </div>
        </Fragment>
    );
}
