import React, {
    useState,
    useEffect,
    useRef
} from "react";

/* Styles */
import './fadeSlider.scss'

export function FadeSlider({ interval = 7000, autoPlay = true }) {
    const slides = [
            "https://65e50a04-a-62cb3a1a-s-sites.googlegroups.com/site/iealfredorebazaacosta/home/IMG_0099.jpg?attachauth=ANoY7crZJbibQD7qi2QJjb88uwqrNt3CS2Bs7MHj-dgVkMUmmipz-7Kk1wnJxiok6ODXy1JjFDGx87toB-dEtNHJh-5yaStiZhStRj_Y5lSNqkPLfO_FcBEYUsWCAbOMcb97nJHjDlV13qy8sPI8AxrsdvWGtu5HsL0RhdZV_Jrjo0vStI8F_86V2Oa_p_GtRnsmj6FrUWmC2yXnuGzWdVGdvtVqy9P9YXkhCCymtjsEEJyLwZfTMhw%3D&attredirects=0",
            "http://iealfredorebazaacosta.edu.pe/wp-content/uploads/2021/04/ara-2.jpeg",
            "http://iealfredorebazaacosta.edu.pe/wp-content/uploads/2021/04/IMG_20190524_114645-1024x768.jpg",
            "https://scontent.flim30-1.fna.fbcdn.net/v/t1.18169-9/1374047_749851625052474_5910419035944427687_n.jpg?_nc_cat=102&ccb=1-7&_nc_sid=e3f864&_nc_eui2=AeGMsyoqQEwEsl4k313FjkMEpqcJj_jfcRWmpwmP-N9xFXi6iL8-jCQVUb-wemmaqaR7VteQBEwErRhaaUOUNlgp&_nc_ohc=gW3HQ2Fy3r4AX-M0ik_&_nc_ht=scontent.flim30-1.fna&oh=00_AT_tsIUYcSbFkQ98jkqoz8PQDeo1hOGYdJuKVsWMcqcaDw&oe=633C1961",
    ];

    const [currentSlide, setCurrentSlide] = useState(0);
    const slideInterval = useRef();
    const itemSlid = <img src={slides[currentSlide]} className="fadeFrame" />;

    const startSlideTimer = () => {
        if (autoPlay) {
            stopSlideTimer();
            slideInterval.current = setInterval(() => {
                setCurrentSlide(currentSlide => currentSlide < slides.length - 1 ? currentSlide + 1 : 0)
            }, interval)
        }
    }

    const stopSlideTimer = () => {
      if (autoPlay && slideInterval.current) {
        clearInterval(slideInterval.current)
      }
    }

    useEffect(() => {
        startSlideTimer();
        console.log(currentSlide);
        return () => stopSlideTimer();
    })

    return (
        <div className="fadeSlider">
            {itemSlid}
        </div>
    );
};
