import { useState, useEffect } from 'react'

/* Styles */
import './opinionPagination.scss';

export function OpinionPagination({ page, setPage, max }) {
    const [input, setInput] = useState(1);

    function chgPage(pos) {
        if (page >= max & pos == 1) return;
        if (page === 1 & pos == -1) return;

        return setPage(page + pos);
    };

    function onKeyDown(e) {
        if (e.keyCode == 13) {
            setPage(parseInt(e.target.value));

            if (
                parseInt(e.target.value < 1) ||
                parseInt(e.target.value > Math.ceil(max)) ||
                isNaN(parseInt(e.target.value))
            ) {
                setPage(1);
                setInput(1);
            } else {
                setPage(parseInt(e.target.value));
            }
        }
    };

    function onChange(e) {
        setInput(e.target.value);
    }

    return (
        <div className="pagination">
            <button onClick={() => setPage(1)}>&#60;&#60;</button>
            <button onClick={() => chgPage(-1)}>&#60;</button>
            <input
                type="text"
                value={page}
                onKeyDown={(e) => onKeyDown(e)}
                onChange={(e) => onChange(e)}
                />
            <button>{max}</button>
            <button onClick={() => chgPage(1)}>&#62;</button>
            <button onClick={() => setPage(max)}>&#62;&#62;</button>
        </div>
    );
}
