import React, {
    Fragment,
    useState,
    useEffect,
    useRef
} from "react";

/* Components */
import { CarouselItem } from "./CarouselItem";
import { CarouselControls } from "./CarouselControls";
import { CarouselIndicators } from "./CarouselIndicators";

/* Styles */
import './slider.scss'

const slides = [
    "https://areajugones.sport.es/wp-content/uploads/2021/02/anime-2021.jpg",
    "https://www.infobae.com/new-resizer/Co9XongQFzgTf8QWzY3vuwtH-u4=/1200x628/filters:format(webp):quality(85)//cloudfront-us-east-1.images.arcpublishing.com/infobae/U2ZQMJTL5ZCQTMY2BACHGQ22AY.png",
    "https://www.nintenderos.com/wp-content/uploads/2022/07/animes.jpg",
    "https://as01.epimg.net/meristation/imagenes/2021/03/13/reportajes/1615633069_923466_1616262218_noticia_normal.jpg",
    "https://es.web.img3.acsta.net/newsv7/20/06/13/10/25/1846086.jpg",
];

export function Slider() {
    const [sliderPos, setSliderPos] = useState(0);
    

    useEffect(() => {
        const slideInterval = setInterval(() => {
            setSliderPos(
                sliderPos => sliderPos < slides.length - 1 ? sliderPos + 1 : 0
            );
        }, 3000)

        return () => clearInterval(slideInterval)
    });

    function goLeft() {
        if (sliderPos == 0)
        {
            setSliderPos(slides.length - 1);
            return;
        }
        setSliderPos(sliderPos - 1);
    };

    function goRight() {
        if (sliderPos == slides.length - 1)
        {
            setSliderPos(0);
            return;
        }
        setSliderPos(sliderPos + 1);
    };

    function goToPos(pos) {
        setSliderPos(pos);
    };

    function CarruselItem({ slide }) {
        return (
            <div className="carruselItem">
                <img className="imgCarrusel" src={slide} />
            </div>
        );
    }

    function Carrusel() {
        return (
            <div className="carrusel">
                <div className="carruselInner"
                    style={{
                        transform: `translateX(${-sliderPos * 100}%)`
                    }}
                >
                    {slides.map((slide, index) => (
                        <CarruselItem slide={slide} key={index} />
                    ))}
                </div>
            </div>
        );
    }

    return (
        <Fragment>
            <div className="carruselContent">
                <Carrusel />
                <div className="goLeft" onClick={goLeft}>{sliderPos}</div>
                <div className="goRight" onClick={goRight}></div>
                
                <div className="carruselPagination">
                    <div className="cpDot" onClick={() => goToPos(1)}></div>
                    <div className="cpDot" onClick={() => goToPos(2)}></div>
                    <div className="cpDot" onClick={() => goToPos(3)}></div>
                    <div className="cpDot" onClick={() => goToPos(4)}></div>
                    <div className="cpDot" onClick={() => goToPos(5)}></div>
                </div>
            </div>
        </Fragment>
    );
}
