import { useState, useEffect, useRef } from 'react'

/* Styles */
import './opinionCard.scss';

export function OpinionCard({ imagen, personLvl, title, description, setWidth }) {
    const refItem = useRef(null);

    useEffect(() => {
        window.addEventListener('resize', setWidth(refItem.current.offsetWidth));

        setWidth(refItem.current.offsetWidth);

        return () => {
            window.removeEventListener('resize', setWidth(refItem.current.offsetWidth));
        };
    }, [window.innerWidth]);
    
    return (
        <div className="cards" ref={refItem}>
            <div className="card">
                <img src={imagen} alt=""/>
                <div className="card-content">
                    <h3>{personLvl}</h3>
                    <h1>{title}</h1>
                    <p>{description}</p>
                </div>
            </div>
        </div>
    );
}
